package com.karmadeliworks.paymentprocessing;import android.content.Context;import android.content.Intent;import android.net.Uri;import android.os.Bundle;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentTransaction;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.TextView;import java.util.Map;public class AlertFragment extends Fragment {    @Override    public void onDestroyView() {        super.onDestroyView();        Utility.flag = "";        Utility.alertSummoned = false;    }    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup container,                             Bundle savedInstanceState) {        View view = inflater.inflate(R.layout.fragment_alert, container, false);        TextView header = view.findViewById(R.id.alertHeader);        TextView body = view.findViewById(R.id.alertBody);        Button dismiss = view.findViewById(R.id.dismissFrag);        if (Utility.flag == "moneySent") {            AlertMessages prompt = new AlertMessages();            Map<String, Map<String, String>> successDict = prompt.getAlertDict();            Map<String, String> successMsg = successDict.get("moneySent");            header.setText(successMsg.get("title"));            body.setText(successMsg.get("message").toString());        } else if (Utility.flag == "failure") {            AlertMessages prompt = new AlertMessages();            Map<String, Map<String, String>> failDict = prompt.getAlertDict();            Map<String, String> failMsg = failDict.get("failure");            header.setText(failMsg.get("title"));            body.setText(failMsg.get("message").toString());        }        dismiss.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                dismissFragment(R.id.frag);                if(Utility.flag == "moneySent"){                    dismissFragment(R.id.frag);                    Intent intent = new Intent(getContext(), MainActivity.class)                            .addFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent                                    .FLAG_ACTIVITY_CLEAR_TASK);                    startActivity(intent);                }                else{                    dismissFragment(R.id.frag);                }            }        });        return view;    }    void dismissFragment(int fragmentID) {        FragmentManager fragMan = getFragmentManager();        Fragment fragment = fragMan.findFragmentById(fragmentID);        FragmentTransaction trans = fragMan.beginTransaction().remove(fragment);        trans.commit();    }}