package com.karmadeliworks.paymentprocessingclass AlertMessages {    var alertDict = mutableMapOf<String, MutableMap<String, String>>()    init{        alertDict["moneySent"] = mutableMapOf("message" to "Congratulations, you just sent $"+ Utility.amountSent +" USD. A receipt has been sent to the email address you provided. Thank you.", "title" to "Money Sent!")        alertDict["failure"] = mutableMapOf("message" to "Something went wrong in trying to send " +                "your money. Please ensure you put down a valid email address.",                "title" to                        "Failed to Send")    }}